import {
  ArrowUpOutlined,
  DollarOutlined,
  ShoppingOutlined,
  UserOutlined,
} from "@ant-design/icons";
import { Avatar, Card, Col, List, Row, Statistic, Typography } from "antd";

import React from "react";

const { Title } = Typography;

const Dashboard: React.FC = () => {
  const recentActivities = [
    {
      id: 1,
      title: "User John Doe logged in",
      icon: <UserOutlined style={{ color: "#1890ff" }} />,
      timestamp: "2 hours ago",
    },
    {
      id: 2,
      title: 'New product "Smartphone X" added',
      icon: <ShoppingOutlined style={{ color: "#52c41a" }} />,
      timestamp: "4 hours ago",
    },
    {
      id: 3,
      title: "Order #12345 completed",
      icon: <DollarOutlined style={{ color: "#faad14" }} />,
      timestamp: "6 hours ago",
    },
    {
      id: 4,
      title: "User Jane Smith updated profile",
      icon: <UserOutlined style={{ color: "#1890ff" }} />,
      timestamp: "8 hours ago",
    },
  ];

  return (
    <div>
      <Title level={2} style={{ marginBottom: 24 }}>
        Dashboard
      </Title>
      <Row gutter={[16, 16]}>
        <Col xs={24} sm={24} md={8}>
          <Card hoverable>
            <Statistic
              title="Users"
              value={1128}
              prefix={<UserOutlined style={{ marginRight: 8 }} />}
              valueStyle={{ color: "#3f8600" }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={24} md={8}>
          <Card hoverable>
            <Statistic
              title="Products"
              value={93}
              prefix={<ShoppingOutlined style={{ marginRight: 8 }} />}
              valueStyle={{ color: "#1677ff" }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={24} md={8}>
          <Card hoverable>
            <Statistic
              title="Revenue"
              value={25600}
              precision={2}
              prefix={<DollarOutlined style={{ marginRight: 8 }} />}
              suffix="$"
              valueStyle={{ color: "#3f8600" }}
            />
          </Card>
        </Col>
      </Row>
      <Row style={{ marginTop: 16 }}>
        <Col span={24}>
          <Card
            title="Recent Activity"
            hoverable
            bodyStyle={{ padding: "0 24px" }}
          >
            <List
              itemLayout="horizontal"
              dataSource={recentActivities}
              renderItem={(item) => (
                <List.Item>
                  <List.Item.Meta
                    avatar={<Avatar icon={item.icon} />}
                    title={item.title}
                    description={item.timestamp}
                  />
                </List.Item>
              )}
            />
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default Dashboard;
